INSERT INTO serie (id, title, img_url, img_url_preview, year_start, year_end, seasons, description, url, media_type ) VALUES (1,'The Walking Dead','/img/media/twd_cover.jpg','/img/media/twd_preview.jpg',2010,2018,9,'The Walking Dead» está ambientada en un futuro apocalíptico con la Tierra devastada por el efecto de un cataclismo, que ha provocado la mutación en zombies de la mayor parte de los habitantes del planeta. La serie, explora las dificultades de los protagonistas para sobrevivir en un mundo poblado por el horror, así como las relaciones personales que se establecen entre ellos, en ocasiones también una amenaza para su supervivencia.','/api/series/1',2);
INSERT INTO serie (id, title, img_url, img_url_preview, year_start, year_end, seasons, description, url, media_type ) VALUES (2,'Prison Break','/img/media/pb_cover.jpg','/img/media/pb_preview.jpg',2005,2017,5,'Prison Break, también conocida como Prison break, en busca de la verdad en Hispanoamérica, fue una serie de televisión dramática estadounidense que estrenó la cadena norteamericana Fox el 29 de agosto de 2005 y terminó el 24 de mayo de 2009 en su cuarta temporada (los dos episodios finales fueron editados directamente al DVD en EE. UU. el 21 de julio, pero el 24 de mayo fueron estrenados por una televisión israelí). La trama de la serie gira en torno a un hombre sentenciado a la pena de muerte y el elaborado plan de su hermano para salvarlo de la misma.','/api/series/2',2);
INSERT INTO serie (id, title, img_url, img_url_preview, year_start, year_end, seasons, description, url, media_type ) VALUES (3,'The Umbrella Academy','/img/media/tua_cover.jpg','/img/media/tua_preview.jpg',2019,2019,1,'Narra la vida de los miembros separados de una familia de superhéroes llamada Umbrella Academy -Monocle, Spaceboy, Kraken, Rumor, Séance, Number Five, Horror y White Violin-, quienes trabajan juntos para resolver la misteriosa muerte de su padre mientras se enfrentan a muchos conflictos debido a sus personalidades y habilidades divergentes.​','/api/series/3',2);
INSERT INTO serie (id, title, img_url, img_url_preview, year_start, year_end, seasons, description, url, media_type ) VALUES (4,'Proyecto Libro Azul','/img/media/pla_cover.jpg','/img/media/pla_preview.jpg',2019,2019,1,'Una crónica de las verdaderas investigaciones de alto secreto patrocinadas por la Fuerza Aérea de los EE. UU. Sobre fenómenos relacionados con los OVNI en los años 50 y 60, conocida como «Proyecto Libro Azul».','/api/series/4',2);
INSERT INTO serie (id, title, img_url, img_url_preview, year_start, year_end, seasons, description, url, media_type ) VALUES (5,'Buenos presagios','/img/media/bp_cover.jpg','/img/media/bp_preview.jpg',2019,2019,1,'El mundo está al borde del apocalipsis mientras la humanidad se prepara para su juicio final. Pero Azirafel, un ángel algo quisquilloso, y Crowley, un demonio, no están entusiasmados con el fin del mundo, y no pueden encontrar al Anticristo.','/api/series/5',2);
INSERT INTO serie (id, title, img_url, img_url_preview, year_start, year_end, seasons, description, url, media_type ) VALUES (6,'Lucifer','/img/media/lucifer_cover.jpg','/img/media/lucifer_preview.jpg',2016,2018,3,'La serie se centrará en Lucifer (Tom Ellis) quien, aburrido e infeliz como el Señor del Infierno, dimite de su trono y abandona su reino para trasladarse a la ciudad de Los Angeles y abrir un lujoso piano-bar llamado Lux. Una vez allí ayudará a la policía a castigar a los más peligrosos criminales de la ciudad.','/api/series/6',2);
INSERT INTO serie (id, title, img_url, img_url_preview, year_start, year_end, seasons, description, url, media_type ) VALUES (7,'Grimm','/img/media/grimm_cover.jpg','/img/media/grimm_preview.jpg',2011,2017,6,'¿Recuerdas los cuentos de hadas de tus padres te contaban antes de acostarte? Esas no eran historias, eran advertencias. Nick Burkhardt pensaba que realmente se había preparado para trabajar como detective de homicidios hasta que empezó a ver cosas que no podía explicar. Cuando su tía enferma Marie llega, la vida de Nick da un giro cuando ella revela que son descendientes de un grupo de élite de cazadores, también conocidos como «Grimms», que luchan para mantener el equilibrio de la humanidad a salvo de las criaturas sobrenaturales del mundo. Como Nick profundiza en su pasado, se da cuenta que tendrá que asumir la responsabilidad de sus antepasados y lidiar con una mitología más grande que la vida de los hermanos Grimm que ahora es demasiado real.','/api/series/7',2);
INSERT INTO serie (id, title, img_url, img_url_preview, year_start, year_end, seasons, description, url, media_type ) VALUES (8,'Anatomía según gray ','/img/media/grey_cover.jpg','/img/media/grey_preview.jpg',2005,2019,15,'La vida de Meredith Grey no es nada fácil. Intenta tomar las riendas de su vida, aunque su trabajo sea de esos que te hacen la vida imposible. Meredith es una cirujana interna de primer año en el Hospital Grace de Seattle, el programa de prácticas más duro de la Facultad de Medicina de Harvard. Y ella lo va a comprobar. Pero no estará sola. Un elenco de compañeros de promoción tendrán que superar la misma prueba. Ahora están en el mundo real, son doctores del hospital. Y en un mundo donde la experiencia en el trabajo puede ser un factor de vida o muerte, todos ellos tendrán que lidiar con los altibajos de sus vidas personales.','/api/series/8',2);
